    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        MainClass nick = new MainClass();
        String id,name,mobile,email;
        id = idtf.getText();
        // Connect and fetch values
        nick.dbConnectExecute3("select * from library_users where Uid = '"+id+"';", "Name", "Mobile", "Email");
        String rsexception = nick.dbConnectExecute3RsError;
        if (rsexception == null){
           name = nick.dbConnectExecute3Out1;
           mobile = nick.dbConnectExecute3Out2;
           email = nick.dbConnectExecute3Out3;
           idtf.setEditable(false);
           nametf.setEditable(true);
           nametf.setText(name);
           mobiletf.setEditable(true);
           mobiletf.setText(mobile);
           emailtf.setEditable(true);
           emailtf.setText(email);
           jButton1.setEnabled(false);
           jButton4.setEnabled(true);
           // Spill Out Error
           String catcherr;
           catcherr = nick.dbConnectExecute3Err;
           if (catcherr != null){
               nick.showMessage(catcherr);
           }
        }
        else {
            nick.showMessage("Sorry, I was not able to find any user with that ID");
        }
       
    }//GEN-LAST:event_jButton1ActionPerformed

