    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
     MainClass nick = new MainClass();
     String id,name,mob,eml;
     id = idtf.getText();
     name = nametf.getText();
     mob  = mobiletf.getText();
     eml = emailtf.getText();
     nick.checkIfEmpty(id, name, mob, eml);
     if ("not_empty".equals(nick.emptyCheckResult)){
         nick.dbConnectUpdate("update library_users set Name = '"+name+"' where Uid ='"+id+"';");
         nick.dbConnectUpdate("update library_users set Mobile = '"+mob+"' where Uid ='"+id+"';");
         nick.dbConnectUpdate("update library_users set Email = '"+eml+"' where Uid ='"+id+"';");
         String err = nick.dbConnectUpdateErr;
         if (err != null){
             nick.showMessage(err);
         }
         else {
             nick.showMessage("Records Updated Successfully");
             dispose();
             new edit_members().setVisible(true);
         }
     }
     else {
         nick.showMessage("Please Fill Out All The Fields.");
     }
    }//GEN-LAST:event_jButton4ActionPerformed

