    private void updatebtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_updatebtnActionPerformed
        MainClass nick = new MainClass();
        String u,p;
        u = tfuser.getText();
        p = new String (tfpass.getPassword());
        nick.toHash(p);
        String hashedPassword = nick.hashot;
        nick.dbConnectUpdate("truncate logon;");
        nick.dbConnectUpdate("insert into logon values ('"+u+"','"+hashedPassword+"');");
        nick.showMessage("Your Values Has Been Updated as follows :-"+"\n"+"User Name: "+u+"\n"+"Password: "+p+"\n"+"Restart Program");
        System.exit(0);
        String con_err = nick.dbConnectUpdateErr;
        if (con_err != null){
            nick.showMessage(con_err);
        }
    }//GEN-LAST:event_updatebtnActionPerformed

