    @SuppressWarnings("static-access")
    private void formWindowOpened(java.awt.event.WindowEvent evt) {//GEN-FIRST:event_formWindowOpened
      MainClass nk = new MainClass();
        if (nk.unstat == 1){
            updatebtn.setEnabled(true);
            unlockbtn.setEnabled(false);
            tfuser.setEditable(true);
            tfpass.setEditable(true);
            jLabel1.setText("Your new username");
            jLabel2.setText("Your new password");
            tfuser.requestFocus();
        }
        else{
        // To append values
        nk.dbConnectExecute2("select * from logon", "alpha", "bravo");
        String u2s,p2s;
        u2s = nk.dbConnectExecute2Out1;
        p2s = nk.dbConnectExecute2Out2;
        tfuser.setText(u2s);
        tfpass.setText(p2s);
        // Spill out error
        String e = nk.dbConnectExecute2Err;
        if (e!=null){
            nk.showMessage(e);
        }
        }
    }//GEN-LAST:event_formWindowOpened

